/*
 * Convert given Hour Minutes Seconds for seconds
 * @param time
 */
function convertTimeToSeconds($time)
{
    $time = date('H:i:s', strtotime($time));
    $explodeValues = explode(':', $time);
    return (($explodeValues[0] * 60 * 60) + ($explodeValues[1] * 60) + $explodeValues[2]);
}

