 * Using in:- CallTakerBookingController@dispatchInformation, BookingController@update, CallTakerBookingRequest@all
 *
 * @param $input
 * @param $keys
 * @return mixed
 */
function convertStringToUpperCase($input, $keys)
{
    foreach ($keys as $key) {
        if (!empty($input[$key])) {
            $input[$key] = strtoupper($input[$key]);
        }
    }
    return $input;
}


